import React from 'react'
import Message from './components/Message'
import axios from 'axios'


class App extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            messages: [],
            newMessage: '',
            newName: ''
        }
        this.handleMessageChange = this.handleMessageChange.bind(this)
        this.handleNameChange = this.handleNameChange.bind(this)
        this.addMessage = this.addMessage.bind(this)

        console.log('constructor')
    }

    componentDidMount() {
        console.log('will mount')
        this.getMessages()
        
    }

    async getMessages() {
        const url = 'http://localhost:8000/getArray'
        const response = await fetch(url)
        const data = await response.json()
        this.setState({messages: data})
    }

    async addMessage(event) {
        
        const messageObject = {
            content: this.state.newMessage,
            username: this.state.newName,
            id: this.state.messages.length + 1
            
        }
        console.log(messageObject)
        axios.post('http://localhost:8000/getArray', messageObject)
            
    }

    handleMessageChange = (event) => {
        console.log(event.target.value)
        this.setState({newMessage: event.target.value})
    }
    
    handleNameChange = (event) => {
        this.setState({newName: event.target.value})
    }

    render() {
        console.log('render')
    
        
        
        
        return (
            <div>
                <h1>ScuffedLauta</h1>
                
                <form onSubmit={this.addMessage}>
                    <input 
                        value={this.state.newMessage}
                        onChange={this.handleMessageChange}
                    />
                    <input
                        value={this.state.newName}
                        onChange={this.handleNameChange}
                    />
                    <button type="submit">
                        tallenna
                    </button>
                </form>
                
                <ul>
                    {this.state.messages.map(content => <Message key={content.id} message={content}/>)}
                </ul>
                
            </div>
        )
    }
}   

export default App
