import React from 'react'
import Message from './components/Message'
import axios from 'axios'


class App extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            messages: [],
            newMessage: '',
            newName: ''
        }
        console.log('constructor')
    }

    componentDidMount() {
        console.log('will mount')
        this.getMessages()
        
    }

    async getMessages() {
        axios
            .get('http://localhost:8000/getArray')
            .then(response => {
              console.log(response)
              this.setState({ messages: response.data })
              
            })
        this.forceUpdate()
    }

    async addMessage(event) {
        
        const messageObject = {
            content: this.state.newMessage,
            username: this.state.newName,
            id: this.state.messages.length
            
        }
        console.log(messageObject)
        axios.post('http://localhost:8000/getArray', messageObject)
            .then(response => {
                console.log(response)
                this.setState({
                    messages: this.state.messages.concat(response.data),
                    newMessage: '',
                    newName: ''
                })
            })
        //event.preventDefault()
    }

    handleMessageChange = (event) => {
        console.log(event.target.value)
        this.setState({newMessage: event.target.value})
    }
    
    handleNameChange = (event) => {
        this.setState({newName: event.target.value})
    }

    render() {
        console.log('render')
        //console.log(messages)
        
        
        
        return (
            <div>
                <h1>ScuffedLauta</h1>
                
                
                <ul>
                    {this.state.messages.map(content => <Message key={content.id} message={content}/>)}
                </ul>
                <form onSubmit={this.addMessage}>
                    <input 
                        value={this.state.newMessage}
                        onChange={this.handleMessageChange}
                    />
                    <input
                        value={this.state.newName}
                        onChange={this.handleNameChange}
                    />
                    <button type="submit">
                        tallenna
                    </button>
                </form>
            </div>
        )
    }
}   

export default App
